                        rx2_meter_data_ready = false;
                    }

                    num = rx2_meter_current_data;

                    switch (rx2_meter_mode)
                    {
                        case MeterRXMode.SIGNAL_STRENGTH:
                        case MeterRXMode.SIGNAL_AVERAGE:
                            switch ((int)g.DpiX)
                            {
                                case 96:
                                    double s;
                                    if (rx2_above30)
                                    {
                                        if (collapsedDisplay)
                                        {
                                            s = (num + 147) / 6;
                                            if (s <= 9.0F)
                                                pixel_x = (int)((s * 15) + 2);
                                            else
                                            {
                                                double over_s9 = num + 93;
                                                pixel_x = 138 + (int)(over_s9 * 2.10);
