                            break;
                        case MeterRXMode.ADC_L:
                        case MeterRXMode.ADC_R:
                            switch ((int)g.DpiX)
                            {
                                case 96:
                                    if (collapsedDisplay)
                                        pixel_x = (int)(((num + 100) * 2.4) + 24);
                                    else pixel_x = (int)(((num + 100) * 1.2) + 12);
                                    break;
                                case 120:
                                    if (num <= -100.0f)
                                        pixel_x = (int)(0 + (num + 110.0) / 10.0 * 14);
