                        "Slow (Attack 2ms, Hang 1000ms, Decay 500ms)");
                    //comboAGC.BackColor = SystemColors.Window;
                    break;
                case AGCMode.MED:
                    SetupForm.CustomRXAGCEnabled = false;
                    //   SetupForm.RXAGCAttack = 2;
