        private void comboDisplayModeBottom_SelectedIndexChanged(object sender, System.EventArgs e)
        {
            switch (comboDisplayModeBottom.Text)
            {
                case "Spectrum":
                    Display.CurrentDisplayModeBottom = DisplayMode.SPECTRUM;
