            else
            {
                radio.GetDSPRX(1, 0).RXFMSquelchOn = false;
                radio.GetDSPRX(1, 1).RXFMSquelchOn = false;
                radio.GetDSPRX(1, 0).RXAMSquelchOn = false;
                radio.GetDSPRX(1, 1).RXAMSquelchOn = false;
