            //{
            //    this.Height = console_basis_size.Height;
            //    return;
            //}

            int h_delta = this.Width - console_basis_size.Width;
            int v_delta = Math.Max(this.Height - console_basis_size.Height, 0);

            if (this.SetupForm != null)
